package net.sf.esfinge.metadata.locate;

public class MetadataLocationException extends RuntimeException {
	
	public MetadataLocationException(String message) {
		super(message);
	}

	public MetadataLocationException() {
		super();
		// TODO Auto-generated constructor stub
	}

	public MetadataLocationException(String message, Throwable cause, boolean enableSuppression,
			boolean writableStackTrace) {
		super(message, cause, enableSuppression, writableStackTrace);
	}

	public MetadataLocationException(String message, Throwable cause) {
		super(message, cause);
	}

	public MetadataLocationException(Throwable cause) {
		super(cause);
	}

}
